.container_l, .op-img-star-divide-low > a {
    width: auto !important;
}

#content_left {
    width: 93%!important;
    padding-left: 4%!important;
}
#content_left > .c-container, #content_left > [class^="result"] {
    display: block !important;
    width: 48% !important;
    min-width: 568px !important;
    min-height: 116px !important;
    float: left !important;
    margin: 0.4% !important;
    padding: 8px 5px 3px 5px !important;
    table-layout: fixed !important;
    border-collapse: separate !important;
    overflow: hidden !important;
}

.s_tab, .nums, .search_tool_conter, #rs, .to_tieba, .to_zhidao_bottom {
    margin: 0 auto!important;
}

#page, #s_tab {
    padding-left: 0px !important;
    text-align: center!important;
}

body[link='#0000cc'] .s_form {
    width:760px!important;
    margin:0 auto !important;
    transform:translatex(-40px);
}

#content_right, .c-recommend, body > div[class="result-op xpath-log"], #content_left > * {
    display: none !important;
}

/*For Super_preloaderPlus script*/
#sp-fw-container {
    transform:translatex(-200px) translatey(13px);
}

/*去除无用链接*/
#rs, .to_tieba, .to_zhidao_bottom {
    display:none;
}

/* 屏蔽不需要的内容 */

.ec-ad,
.page-banner,
.ad-icon,
.banner-ad,
.top-ads-banner,
.ad-taishan-bar,
.toolsbar-vip-wrap,
.end-download-bottom-layout,
.search-aside-adWrap,
.tiger-shifen-dialog,
.tiger-lossUser-dialog,
.search-aside-newadWrap,
div[class^="ec_src"],
#wkgg,
#xs-task,
#user-my-class,
#share-tips,
#banurl,
#nav-gaokao,
#guess-like-doc,
#lastcell-dialog,
#add-new .frist-item,
#topSearchBox .s_tools,
#doc-aside-border .commerce-flow,
div[id^='wkad'],
div[id^='html-reader-AD'],
div[id^='html-reader-banner'] {
    display: none!important;
}
/* 特殊元素处理 */

#adbg,
#activity-tg .num {
    background: none!important;
}
/*  屏蔽7周年广告 */

.fix-searchbar-wrap,
.bg-opacity {
    top: 0px !important;
}

#packgift,
#pop_lastcell {
    display: none!important;
}

#fengchaoad {
    display: none!important;
}

/* 屏蔽内容 */
div[class*="-ads-"],
.magzine-list,
.footer,
.s-tools,
.new-icon,
.warning-warpper,
.wgt-common-nav .main-menu li:last-child,
.top-search-box ul.channel,
.wgt-feeds-video,
#aside,
#msg-arrow,
#exp-msgbox,
#wgt-left-promo,
#wgt-like {
    display: none!important;
}
/* 调整排版 */
.body-wrapper .content-container {
    animation-name: left_logoR;
    -webkit-animation-duration: .2s;
    -webkit-animation-timing-function: ease;
}
.breadcrumb,
#bread-wrap,
.body-wrapper{
    background-color:#f1f1f1;
}
.body-wrapper .content-container .main-content{
    background-color:#fefefe;
}

.top-search-box .s-nav {
    height: 20px;
}
.userbar {
    top: 30px;
}



.header,
.nav .nav-wrap,
.breadcrumb .bread-wrap,
.content-container {
    width: 1030px;
    margin:0 auto;
}


#main-content {
    padding: 0;
    width: inherit;
    margin:10px auto;
    border-radius: 3px;
    box-shadow: 0 1px 10px rgba(0, 0, 0, 0.12);
    -webkit-box-shadow: 0 1px 10px rgba(0, 0, 0, 0.12);
    -moz-box-shadow:0  1px 10px rgba(0, 0, 0, 0.12);
}

#exp-article>div[class^="exp-"],
#exp-article>div[class^="wgt-"],
.main-content-bottom,
#wgt-comments
{
    padding:10px 50px;
}

.wgt-common-nav .share-exp-e{
    width:192px;
    margin-right:20px;
}

.wgt-thumbs{
	background-size:auto 70px;                              
}
.main-content-bottom {
    border-top: none;
    border: 1px solid rgb(214, 214, 216);
  
}

/*  右侧栏  */
#wgt-exp-share{
    bottom:100px!important;
}

/* 动画效果 */
@-webkit-keyframes left_logoR {
    0% {
        -webkit-transform: translateY(64px);
        opacity: 0;
    }
    50% {
        opacity: 0;
    }
    100% {
        opacity: 1;
    }
}

div[class^=ec_src]{
    display:none;
}

.header{
    width:auto;
}

/* 排版 */

#body{
    margin-bottom:50px;
}

a {
    text-decoration: none!important;
}
.search-box-new{
    height:55px;
}


.qb-section{
    width: 1000px;
    margin: auto;
}
.has-menu .search-box-new .search-cont {
    width: 730px!important;
}
.wgt-header-title .wgt-header-title-content {
    
    width: 1000px;
   
}
.wgt-header-title-btn{
    float:right;
    margin-top:10px;
}

#qb-content {
    padding: 0;
    width: 100%;
    border-radius: 5px;
    box-sizing: border-box;
}
#qb-content>div {
    padding: 0;
    border: none;
    margin: 10px auto;
}
#qb-content>div:first-child {
    margin-top: 0;
}
#qb-content>div:last-child {
    margin-bottom: 0;
}
/* 问答 */

.wgt-ask {
    padding: 10px 20px!important;
}
/* 最好答案 */
div.wgt-best .hd{
    margin-top:0!important;
}
.wgt-best .hd {
    padding: 7px 10px;
}
.wgt-best .bd {
    padding: 0 20px;
}
.wgt-best .wgt-replyer-special-bg {
    border: none;
}
/* 其它回答 */

.wgt-answers {
    padding-bottom: 20px !important;
}
.wgt-answers .hd h2 {
    padding: 10px 20px;
}
.wgt-answers .bd-wrap {
    border: none;
}
.wgt-answers .bd {
    border-bottom: 1px dotted #e9e9e9;
    padding: 24px 20px 0px;
}
.wgt-answers #show-fold-container,
.wgt-answers #show-hide-container,
.wgt-answers .show-answer-dispute {
    height: 40px;
    line-height: 40px;
    margin: 15px 420px 5px;
    border-radius: 60px;
}
.wgt-answers #show-answer-fold,
.wgt-answers #show-answer-hide,
.wgt-answers .show-hide-dispute {
    cursor: pointer;
    font-size: 16px;
}
.wgt-answers #show-answer-fold.hover,
.wgt-answers #show-answer-hide.hover,
.wgt-answers .show-hide-dispute.hover {
    border-radius: 60px;
    text-decoration: none;
}
/* 列表 */

.wgt-related,
.wgt-topic,
.wgt-related .related-list,
.wgt-push {
    padding: 0!important;
}
.wgt-silder-push u1,
.wgt-related .related-list {
    margin: 0;
}
.wgt-topic h2,
.wgt-related h2,
.wgt-push h2 {
    padding: 10px 20px;
}
.wgt-topic ul,
.wgt-related ul,
.wgt-push ul,
.wgt-topic .topic-list,
.wgt-push .push-ul {
    margin: 0px;
    padding: 0px;
}
.wgt-topic ul li,
.wgt-related ul li,
.wgt-push ul li {
    padding: 5px 20px;
}
.wgt-topic ul li:hover a,
.wgt-related ul li:hover span,
.wgt-push ul li:hover span,
.wgt-push ul li:hover a {
    font-weight: bold;
}
/* 调节 */

#qb-content .wgt-push,
#qb-content .push-ul,
#qb-content .wgt-related,
#qb-content .related-list,
#qb-content .wgt-topic,
#qb-content .topic-list {
    margin-top: 0;
    padding-top: 0;
}
#qb-content .wgt-push,
#qb-content .push-ul {
    max-height: 500px;
}

.wgt-best-operator{
    padding-bottom:10px;
}


.f-aid,
a.f-aid {
    color: #333;
}
body .pager a:hover {
    color: #fff;
    border: solid 1px #43cc69;
}


/* 修复排版错位 */
.accuse-area .wgt-accuse {
   
    line-height: 1;
    
}

#answer-bar {
    line-height: 30px;
    
}

.wgt-ask .ask-info .wgt-accuse {
   
    top: 9px;
    
}
.wgt-replyer-all {
    margin-left: 20px;
}

/* 背景和颜色 */

body {
   
    background-color: #f1f2f3;
    background-size: cover;
    /*     filter:blur(5px); */
}
#qb-content>div {
    background: #fff;
}
/* 问答 */

.wgt-ask:hover {
    background: #f5f5f5;
}
/* 最佳答案 */

.wgt-best .hd {
    background: #27ae61;
}
.wgt-best .hd span {
    color: #fff;
}
.wgt-best .wgt-replyer-special-bg {
    background: rgba(218, 224, 228, 0.21);
}
div.wgt-best .answer-title{
    font-size:20px;
    width:100px;
}
/* 其它回答 */

.wgt-answers .hd h2 {
    background: #2aa198;
}
.wgt-answers .hd h2 {
    color: #fff;
}
.wgt-answers .bd:hover {
    background: #f8f8f8;
}
.wgt-answers #show-fold-container,
.wgt-answers #show-hide-container,
.wgt-answers .show-answer-dispute {
    background: #46b39e;
    color: #fff;
}
.wgt-answers #show-answer-fold,
.wgt-answers #show-answer-hide,
.wgt-answers .show-hide-dispute {
    color: #fff;
}
.wgt-answers #show-answer-fold.hover,
.wgt-answers #show-answer-hide.hover,
.wgt-answers .show-hide-dispute.hover {
    background: #1bbc9b;
    color: #fff;
}
/* 列表 */

.wgt-topic h2,
.wgt-related h2,
.wgt-push h2 {
    background: #95a5a5;
}
.wgt-topic h2,
.wgt-related h2,
.wgt-push h2,
.wgt-topic h2 em {
    color: #fff;
}
.wgt-topic li:nth-child(even),
.wgt-related ul li:nth-child(even),
.wgt-push ul li:nth-child(even) {
    background: #f9fafb;
}
.wgt-topic ul li:hover,
.wgt-related ul li:hover,
.wgt-push ul li:hover {
    background: #f5f5f5;
}
.wgt-topic .topic-list li:hover a,
.wgt-push .push-ul li:hover a,
.wgt-topic ul li:hover a,
.wgt-related ul li:hover span,
.wgt-push ul li:hover span,
.wgt-push ul li:hover a {
    color: #333;
}
.wgt-topic ul li span,
.wgt-related ul li span,
.wgt-push ul li span {
    color: #333;
}
/* 调节 */

.f-aid,
a.f-aid {
    color: #333;
}
body .pager b {
    background: #2aa198;
}
body .pager a:hover {
    color: #fff;
    background: #34bab0;
}

/* 特效 */

#qb-content>div {
   border: 1px solid #d8d8d8;
}

.wgt-bottom-ask,
.aside.fixheight,
.shop-entrance,
div[class*="_zwd_"] {
    display: none!important;
}

